import { Machine } from "xstate";
import jikanjs from "jikanjs"; // Uses per default the API version 3

const searchMachine = Machine(
  {
    id: "search",
    context: {
      timer: null,
      query: "",
      results: {},
      error: {},
    },
    initial: "idle",
    states: {
      idle: {
        on: {
          focus: "focused",
        },
      },
      focused: {
        on: {
          keyup: {
            target: "typing",
          },
          blur: "idle",
        },
      },

      typing: {
        entry: ["updateQuery", "clearTimer", "setTimer"],
        on: {
          keyup: {
            target: "typing",
          },
          FETCH: {
            target: "pending",
            cond: "checkQuery",
          },
          blur: {
            target: "idle",
          },
        },
      },
      pending: {
        entry: "goFetch",
        on: {
          keyup: {
            target: "typing",
          },
          RESOLVE: {
            target: "resolved",
          },
          REJECT: {
            target: "rejected",
          },
          blur: {
            target: "idle",
          },
        },
      },
      resolved: {
        on: {
          keyup: {
            target: "typing",
          },
          blur: {
            target: "idle",
          },
        },
      },
      rejected: {
        on: {
          keyup: {
            target: "typing",
          },
          blur: {
            target: "idle",
          },
        },
      },
    },
  },
  {
    actions: {
      setTimer: (context, event) => {
        context.timer = setTimeout(() => {
          service.send("FETCH");
        }, 1000);
      },
      clearTimer: (context, event) => {
        clearTimeout(context.timer);
      },
      goFetch: (context, event) => {
        jikanjs
          .search("anime", context.query, [1])
          .then((response) => {
            console.log(response.results);
            context.results = response.results;
            service.send("RESOLVE");
          })
          .catch((error) => {
            console.error(error);
            context.error = error.message;
            service.send("REJECT");
          });
      },
      updateQuery: (context, event) => {
        context.query = event.target.value;
      },
    },
    guards: {
      checkQuery: (context, event) => context.query != "",
    },
  }
);

exports.default = searchMachine;
